(24, '加入創富會', '主頁管理', '', '&lt;div class=&quot;article-info&quot;&gt;\r\n	&lt;form action=&quot;index.php?m=Home&amp;amp;c=Index&amp;amp;a=JoinFormSubmit&quot; onsubmit=&quot; return check_form(''guestname|guestaddr|guesttel|guestemail|guestgender|guestage|guestbirth'');&quot; name=&quot;Join&quot; id=&quot;chronoform_Join&quot; method=&quot;post&quot; class=&quot;hasValidation&quot;&gt;\r\n		&lt;div class=&quot;jshop_cat_con&quot;&gt;\r\n			&lt;table width=&quot;100%&quot; align=&quot;center&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; class=&quot;enquiry_form&quot;&gt;\r\n				&lt;tbody&gt;\r\n					&lt;tr&gt;\r\n						&lt;td align=&quot;center&quot; class=&quot;icon&quot;&gt;\r\n							&lt;span class=&quot;required&quot;&gt;* &lt;/span&gt; \r\n						&lt;/td&gt;\r\n						&lt;td align=&quot;right&quot; class=&quot;tit&quot;&gt;\r\n							&lt;b&gt;姓名&lt;/b&gt; \r\n						&lt;/td&gt;\r\n						&lt;td&gt;\r\n							&lt;div class=&quot;ccms_form_element cfdiv_text&quot; id=&quot;guestname_container_div&quot;&gt;\r\n								&lt;label for=&quot;guestname&quot;&gt;&amp;nbsp;&lt;/label&gt; &lt;input id=&quot;guestname&quot; maxlength=&quot;150&quot; size=&quot;30&quot; class=&quot; validate[''required'']&quot; title=&quot;&quot; type=&quot;text&quot; value=&quot;&quot; name=&quot;guestname&quot; /&gt; \r\n								&lt;div class=&quot;clear&quot;&gt;\r\n								&lt;/div&gt;\r\n								&lt;div id=&quot;error-message-guestname&quot;&gt;\r\n								&lt;/div&gt;\r\n							&lt;/div&gt;\r\n						&lt;/td&gt;\r\n					&lt;/tr&gt;\r\n					&lt;tr&gt;\r\n						&lt;td align=&quot;center&quot; class=&quot;icon&quot;&gt;\r\n							&lt;span class=&quot;required&quot;&gt;* &lt;/span&gt; \r\n						&lt;/td&gt;\r\n						&lt;td align=&quot;right&quot; class=&quot;tit&quot;&gt;\r\n							&lt;b&gt;地址&lt;/b&gt; \r\n						&lt;/td&gt;\r\n						&lt;td&gt;\r\n							&lt;div class=&quot;ccms_form_element cfdiv_text&quot; id=&quot;input_text_81_container_div&quot;&gt;\r\n								&lt;label&gt;&am